import axios from 'axios';
import Product from "../componets/Product";
import LoadingBox from "../componets/LoadingBox";
import MessageBox from "../componets/MessageBox";
import { useSelector } from "react-redux";

// import data from "../data";

export default function HomeScreen(){


    const productList = useSelector(state => state.productList);
    const {loading: error, product} = pro

    /* Send request to backend */
    useEffect(() => {

        /* define the "fetch function" use the products api to get all product info and store into data array */
        const fetchData = async () => {

            /* try to load products, monitoring loading progress, and catch loading errors */
            try{

                setLoading(true);
                const {data} = await axios.get('/api/products');
                setLoading(false);
                setProducts(data);
            }

            /* loading errors - properly catch and notify user of the errors */
            catch (err){

                setError(err.message);
                setLoading(false);
            }

        };

        /* Call fetch data function - action to build data array */
        fetchData();

    }, []);


    /* Homescreen Redering */
    return (
        <div>
            {loading? (<LoadingBox></LoadingBox>): error?(<MessageBox variant="danger">{error}</MessageBox>):(
