import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addToCart } from "../actions/CartActions";
import MessageBox from "../componets/MessageBox";
import { Link } from "react-router-dom";

export default function CartScreen(props){

    /* get the prodcut id from url params */
    const productId = props.match.params.id;

    /* split out the qty of the url params */
    const qty = props.location.search? Number(props.location.search.split('=')[1]): 1 ;

    /* use to get redux actions from within  */
    const dispatch = useDispatch();

    const cart = useSelector(state => state.cart);

    const {cartItems} = cart;

    /* Send request to backend */
    useEffect(() => {

        if(productId){

            dispatch(addToCart(productId, qty));

        }

    }, [dispatch, productId, qty]);

    /* remove from cart (delete function) */
    const removeFromCart = (id) => {

        //delete
    }

    return(

        <div className="row top">

            <div className="col-2">

                <h1>Shopping Cart</h1>

                {/*  check to see if cart has items in it, if not, provide link to homescreen */}
                {cartItems.length === 0 ? 

                <MessageBox>Cart is empty <Link to="/"> Back to Products</Link> </MessageBox>:
                (
                    <ul>
                        {
                            cartItems.map((item) => (

                                <li key={item.product}>

                                    <div className="row">
                                        <img
                                            src={item.image}
                                            alt={item.name}
                                            className="small"
                                        ></img>
                                    </div>

                                    <div className="mon-30">
                                        <Link to={`/product/${item.product}`}>{item.name}</Link>
                                    </div>

                                    <div>
                                        <select value={item.qty} onChange={e => dispatch(addToCart(item.product), Number(e.target.value))} >

                                            {[...Array(item.countInStock).keys()].map((x) => (

                                              <option key={x + 1} value={x + 1}> {x + 1}
                                              </option>
                                              )
                                            )}

                                        </select>
                                    </div>

                                    <div>${item.price}</div>

                                    <div>
                                        <button type="button" onClick={() => removeFromCart(item.product)}

                                        >Delete</button>
                                    </div>
                                </li>
                            ))}
                    </ul>
                )}
            </div>

            <div className="col-1">

                <div className="card card-body">
                    <ul>
                        <li>
                            <h2>Total ({})</h2>
                        </li>
                    </ul>
                </div>        

            </div>                                    


        </div>
    );
}
