import React, {useEffect, useState} from "react";
import axios from 'axios';
import Product from "../componets/Product";

// import data from "../data";

export default function HomeScreen(){

    /* array to get the product array from the backend - setProducts function builds the array */
    const [products, setProducts] = useState([]);

    /* Send request to backend */
    useLayoutEffect(() => {

        /* define the "fetch function" use the products api to get all product info and store into data array */
        const fetchData = async () => {
            const {data} = await axios.get('/api/products');
            setProducts(data);
        };

        /* Call fetch data function - action to build data array */
        fetchData();

    }, []);

    return (
        <div>
            <div className="row center">
                {data.products.map((product) => (
                    <Product key={product._id} product={product}></Product>
                ))}
            </div>
        </div>
    );
}
