class FavouriteTeam extends Component {
    state = {
      devices: [],
      selectedDevice: "",
      validationError: ""
    };

    componentDidMount() {

        fetch(
            '/api/deviceType'
          )
            .then(response => {
              return response.json();
            })
            .then(data => {
              let deviceFromApi = data.map(device => {
                return { value: device, display: device };
              });
              this.setState({
                teams: [
                  {
                    value: "",
                    display:
                      "(Select your device)"
                  }
                ].concat(teamsFromApi)
              });
            })
            .catch(error => {
              console.log(error);
            });
    }

    render() {
      return (
        <div>
