import { DEVICE_SELECTED, PRODUCTS_DEVICE_SET, PRODUCT_DETAILS_FAIL, PRODUCT_DETAILS_REQUEST, PRODUCT_DETAILS_SUCCESS, PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS } from "../constants/ProductConstants";

/* reducer for product list actions */
export const productListReducer = (state = {loading:true, products: []}, action) => {

    switch(action.type){

        case PRODUCT_LIST_REQUEST:
            return {loading: true};
        case PRODUCT_LIST_SUCCESS:
            return {loading: false, products: action.payload};
        case PRODUCT_LIST_FAIL:
            return {loading: false, error: action.payload};
        default:
            return state;

    }
};

/* reducer for product details actions */
 export const productDetailsReducer = (state = {loading: true, product:{}}, action) => {

     switch (action.type){
         case PRODUCT_DETAILS_REQUEST:
             return {loading: true};
         case PRODUCT_DETAILS_SUCCESS:
             return {loading: false, product: action.payload};
         case PRODUCT_DETAILS_FAIL:
             return {loading: false, error: action.payload};
         default:
             return state;
     }
 };

  /* reducer for user device slection actions */
  export const deviceDropdownReducer = (state = {selectedDevice:1, deviceData: []}, action) => {

    switch (action.type) {
      case DEVICE_SELECTED:
        return { ...state, selectedDevice: action.payload };
      case PRODUCTS_DEVICE_SET:
        return { ...state, deviceData: action.payload };
      default:
        break;
    }
    return state;
  };
