import React, {useEffect, useState} from "react";
import axios from 'axios';
import Product from "../componets/Product";

// import data from "../data";

export default function HomeScreen(){

    /* array to get the product array from the backend - setProducts function builds the array */
    const [products, setProducts] = useState([]);

    /* array to get the enhance user experience - deal with loading progress */
    const [loading, setLoading] = useState(false);

    /* array to get the enhance user experience - deal errors with loading progress */
    const [error, setError] = useState(false);

    /* Send request to backend */
    useEffect(() => {

        /* define the "fetch function" use the products api to get all product info and store into data array */
        const fetchData = async () => {

            /* try to load products, monitoring loading progress, and catch loading errors */
            try{

                setLoading(true);
                const {data} = await axios.get('/api/products');
                setLoading(false);
                setProducts(data);
            }

            /* loading errors - properly catch and notify user of the errors */
            catch (error){

                setError(err.message);
                setLoading(false);
            }

        };

        /* Call fetch data function - action to build data array */
        fetchData();

    }, []);

    return (
        <div>
            {loading? (<LoadingBox></LoadingBox>): error?(
            <MessageBox>{error}</MessageBox>
            ):(
            <div className="row center">
                {products.map((product) => (
                    <Product key={product._id} product={product}></Product>
                ))}
            </div>
            )}
        </div>
    );
}
