import { ADD_DROPDOWN_VALUE, SET_DROPDOWN_VALUES } from "../constants/ProductConstants"


let options= [
    {key: 1, 
     text: "this is what's displayed", 
     value: "this is the event value"},

     {key: 2,
     text: "option2",
     value: "value of option 2"}
]

//manages the options array
export const optionsReducer = (state= options, action)=>{
    switch(action.type){
        case ADD_DROPDOWN_VALUE:
            return [...state, action.payload]
        default:
        return state
    } 
}
