import React, {useEffect} from "react";
// import Axios from 'axios';
import Product from "../componets/Product";
import LoadingBox from "../componets/LoadingBox";
import MessageBox from "../componets/MessageBox";
import {useDispatch, useSelector } from "react-redux";
import { listProducts} from "../actions/ProductActions";
import DeviceDropDown from "../componets/DeviceDropDown";

// import data from "../data";

export default function HomeScreen(){

    /* use to get redux actions from within  */
    const dispatch = useDispatch();

    /* get prouductlist from redux store  */
    const productList = useSelector((state) => state.productList);

    /* get data from the product list */
    const {loading, error, products} = productList;

    // const [loading, setLoading] = useState(false);

    // const [products, setProducts] = useState([]);

    // const[error, setError] = useState(false);

    /* Send request to backend (Should run once) */
    useEffect(() => {

        dispatch(listProducts());


        /* define the "fetch function" use the products api to get all product info and store into data array */
        // const fetchData = async () => {

        //     /* try to load products, monitoring loading progress, and catch loading errors */
        //     try{

        //         // setLoading(true);
        //         const {data} = await Axios.get('/api/products');
        //         // setLoading(false);
        //         // setProducts(data);
        //     }

        //     /* loading errors - properly catch and notify user of the errors */
        //     catch (err){

        //         // setError(err.message);
        //         // setLoading(false);
        //     }

        // };

        // /* Call fetch data function - action to build data array */
        // fetchData();

    }, [dispatch]);

    /* Homescreen Redering */
    return (

       <div>
            <h2>Device Type</h2>
                <select>
                    <option selected="true"> Select Device</option>
                    <option>Routers</option>
                    <option>Modems</option>
                    <option>Laptops</option>
                    <option>Webcams</option>
                </select>

                <div class="dropdown">
                <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenu2" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                    Dropdown
                </button>
                <div class="dropdown-menu" aria-labelledby="dropdownMenu2">
                    <button class="dropdown-item" type="button">Action</button>
                    <button class="dropdown-item" type="button">Another action</button>
                    <button class="dropdown-item" type="button">Something else here</button>
                </div>
                </div>


            {/* if loading is true - render data, if not display error message */}
            {loading? (<LoadingBox></LoadingBox>): error?(<MessageBox variant="danger">{error}</MessageBox>):(

            <div className="row center">

                {products.map((product) => (
                    <Product key={product._id} product={product}></Product>
                ))}
            </div>
            )}
        </div>


    );
}
