import React, { useEffect, useState} from "react";
import { ThemeConsumer } from "react-bootstrap/esm/ThemeProvider";
import { render } from "react-dom";
import Product from "./Product";

export default class PriceSelection extends React.Component{

    constructor(props) {

            super(props);
            this.state = {   
                prices:[],
                price:"",

            };

            this.handlePriceSubmit = this.handlePriceSubmit.bind(this);
        }


    /* track value of device state when submit is clicked */
    handlePriceSubmit(event){

        alert('Your selected value is ' + this.state.price);
        event.preventDefault();
    }


    /* track value of state when new slection occurs */
    handlePriceChange = event => {

        this.setState({price: event.target.value});

    }

    /* Check to make sure we do not have duplicate objects for filtering */
    getUnique(arr, comp){

        const unique = arr

        /* store the values to compare into the array */
        .map(e => e[comp])

        /* store the unique object */
        .map((e,i, final) => final.indexOf(e) === i && i)

        /* remove the dups and keep only the unique values */
        .filter(e => arr[e])

        /* map the unique onject */
        .map(e => arr[e]);

        return unique;
    }

    componentDidMount(){

        const prices = require('./priceDropdown.json');
        this.setState({prices: prices});

        const products = require('./productsTest.json');
    }


    render(){

        // const devices = require('./devicesTest.json');
        // const uniqueDevice = this.getUnique(devices.type, "name");



        /* Products - price filtering */
        const prices = this.state.prices;
        const price = this.state.price;
        // const uniquePrices = this.getUnique(this.state.prices, "range");
        // const filterPriceDropdown = prices.filter(function(result){
        //     return result.price === price;
        // });


        return(

        <form onSubmit={this.handlePriceSubmit}>


                <div className="rowC">
                <label>Select Device  </label>
                    <select>
                            <option>Device Type</option>
                        </select>
                </div>

            {/* <label>Select Price Range </label>
                <select
                    value={this.state.price}
                    onChange={this.handlePriceChange}
                    >
                    {uniquePrices.map(price => (
                        <option key={price.price_range} value={price.price_range}>
                            {prices.price_range}
                        </option>
                    ))}
                </select> */}
            <div className="row center">

                <div className="rowC">
                <label>Select Price Range </label>
                <select
                    value={this.state.price}
                    onChange={this.handlePriceChange}
                    >
                    {prices.map(price => (
                        <option key={price.range} value={price.range}>
                            {price.range}
                        </option>
                    ))}
                </select>

                </div>

                <div className="rowC">
                <label>Select Features  </label>
                    <select>
                            <option>Features</option>
                        </select>
                </div>

                <div className="rowC">
                <label>Select Brand  </label>
                    <select>
                            <option>Brand</option>
                        </select>
                </div>

                <div className="rowC">
                    <label>Select Ratings </label>
                        <select>
                            <option>Ratings</option>
                        </select>
                </div>
            </div>
            <div>

                {products.map(product => (

                    <Product key={product.price_range} price={price}></Product>

                ))}
            </div>
            <br />
            <br />
        </form>

        );
    }
}
