import React, { useEffect, useState} from "react";
import { ThemeConsumer } from "react-bootstrap/esm/ThemeProvider";
import { render } from "react-dom";
import Product from "./Product";

export default class Test2 extends React.Component{

    constructor(props) {

            super(props);
            this.state = {   
                products:[],
                product:"",
                prices:[],
                price:"",

            };

            this.handleSubmit = this.handleSubmit.bind(this);
        }


    /* track value of state when submit is clicked */
    handleSubmit(event){

        alert('Your selected value is ' + this.state.product);
        event.preventDefault();
    }

    /* track value of state when new slection occurs */
    handleChange = event => {

        this.setState({product: event.target.value});
        this.setState({price: event.target.value});
        this.setState({brand: event.target.value});
        this.setState({feature: event.target.value});
        this.setState({rating: event.target.value});

    }

    /* Check to make sure we do not have duplicate objects for filtering */
    getUnique(arr, comp){

        const unique = arr

        /* store the values to compare into the array */
        .map(e => e[comp])

        /* store the unique object */
        .map((e,i, final) => final.indexOf(e) === i && i)

        /* remove the dups and keep only the unique values */
        .filter(e => arr[e])

        /* map the unique onject */
        .map(e => arr[e]);

        return unique;
    }

        componentDidMount(){

            // const devices = require('./devicesTest.json');
            // this.setState({devices: devices});

            const products = require('./productsTest.json');
            this.setState({products: products});

            // const prices = require('./priceDropdown.json');
            // this.setState({prices: prices})

            // const brands = require('./brandDropdown.json');
            // this.setState({brands: brands})

            // const features = require('./featuresDropdown.json');
            // this.setState({features: features})

            // const ratings = require('./ratingsDropdown.json');
            // this.setState({ratings: ratings})

        }


    render(){

        // const devices = require('./devicesTest.json');
        // const uniqueDevice = this.getUnique(devices.type, "name");

        /* Products - device filtering */
        const products = this.state.products;
        const product = this.state.product;
        const uniqueProducts = this.getUnique(this.state.products, "category");
        const filterDeviceDropdown = products.filter(function(result){
            return result.category === product;
        });

        /* Products - price filtering */
        // const prices = this.state.prices;
        // const price = this.state.price;
        // const uniqueProducts = this.getUnique(this.state.prices, "range");
        // const filterPriceDropdown = prices.filter(function(result){
        //     return result.priceFilter.price === price;
        // });


        return(

        <form onSubmit={this.handleSubmit}>
            <label>Select Device  </label>
                <select
                    value={this.state.product}
                    onChange={this.handleChange}
                    >
                    {uniqueProducts.map(product => (
                        <option key={product._id} value={product.category}>
                            {product.category}
                        </option>
                    ))}
                </select>
            <div className="row center">

                <div className="rowC">
                    <label>Select Price  </label>
                        <select>
                            <option>Price</option>
                        </select>

                </div>

                <div className="rowC">
                <label>Select Features  </label>
                    <select>
                            <option>Features</option>
                        </select>
                </div>

                <div className="rowC">
                <label>Select Brand  </label>
                    <select>
                            <option>Brand</option>
                        </select>
                </div>

                <div className="rowC">
                    <label>Select Ratings</label>
                        <select>
                            <option>Ratings</option>
                        </select>
                </div>
            </div>

            {/* <input type='submit' value="Submit" /> */}
            <div>
            {/* <Product key={product._id} product={product}></Product> */}
                {filterDeviceDropdown.map(product => (
                    // <div key = {product._id}>
                    //     {product.name}
                    <Product key={product._id} product={product}></Product>

                    // </div>
                ))}
            </div>
            <br />
            <br />
        </form>

        );
    }
}
