import React, {useEffect} from "react";
// import Axios from 'axios';
import Product from "../componets/Product";
import LoadingBox from "../componets/LoadingBox";
import MessageBox from "../componets/MessageBox";
import {useDispatch, useSelector } from "react-redux";
import { listProducts, selectDevice } from "../actions/ProductActions";
import DeviceDropDown from "../componets/DeviceDropDown";

// import data from "../data";

export default function HomeScreen(){

    /* use to get redux actions from within  */
    const dispatch = useDispatch();

    /* get prouductlist from redux store  */
    const productList = useSelector((state) => state.productList);

    /* get device list from redux store  */
    const deviceSelect = useSelector((state) => state.deviceSelect);

    // /* get data from the product list */
    // const {loading2, error2, devices} = deviceSelect;

    /* get data from the product list */
    const {loading, error, products} = productList;

    // const [loading, setLoading] = useState(false);

    // const [products, setProducts] = useState([]);

    // const[error, setError] = useState(false);

    /* Send request to backend (Should run once) */
    useEffect(() => {

        dispatch(listProducts());


        /* define the "fetch function" use the products api to get all product info and store into data array */
        // const fetchData = async () => {

        //     /* try to load products, monitoring loading progress, and catch loading errors */
        //     try{

        //         // setLoading(true);
        //         const {data} = await Axios.get('/api/products');
        //         // setLoading(false);
        //         // setProducts(data);
        //     }

        //     /* loading errors - properly catch and notify user of the errors */
        //     catch (err){

        //         // setError(err.message);
        //         // setLoading(false);
        //     }

        // };

        // /* Call fetch data function - action to build data array */
        // fetchData();

    }, [dispatch]);

    useEffect(() => {

        dispatch(selectDevice());

    }, [dispatch]);

    /* Homescreen Redering */
    return (

        <div>
            <div className="row top">
                <div>
                    <div className="col-2">
                        <h2>Device Type</h2>
                        {devices.map((device) => (
                            <DeviceDropDown key={device._id} device={device}></DeviceDropDown>
                ))}
                    </div>
                </div>
            </div>

            <div className="row top">
                <div>
                    <div className="col-2">
                        <h2>Preferences</h2>
                    </div>
                </div>
            </div>

            {/* if loading is true - render data, if not display error message */}
            {loading? (<LoadingBox></LoadingBox>): error?(<MessageBox variant="danger">{error}</MessageBox>):(

            <div className="row center">

                {products.map((product) => (
                    <Product key={product._id} product={product}></Product>
                ))}
            </div>
            )}
        </div>
    );
}
