import Axios from "axios";
import { PRODUCT_DETAILS_FAIL, PRODUCT_DETAILS_REQUEST, PRODUCT_DETAILS_SUCCESS, PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS } from "../constants/ProductConstants"

/* Actions */

/* list products from backend */
export const listProducts = () => async (dispatch) => {

    dispatch({type: PRODUCT_LIST_REQUEST});

    try{

        const {data} = await Axios.get('/api/products');
        dispatch({type: PRODUCT_LIST_SUCCESS, payload:data});

    }

    catch(err){

        dispatch({type:PRODUCT_LIST_FAIL, payload:err.message});
    }
};

/* get product details from backend */
export const detailsProduct = (productId) => async (dispatch) => {

    dispatch({type: PRODUCT_DETAILS_REQUEST, payload: productId});

    try{

        const {data} = await Axios.get(`/api/products/${productId}`);
        dispatch({type: PRODUCT_DETAILS_SUCCESS, payload:data});
    }

    /* product lookup failure */
    catch(err){

        dispatch({type: PRODUCT_DETAILS_FAIL, payload: 

            /*  backend failure */
            err.response && err.response.data.message 

            /* render the error message from backend */
            ? err.resonse.data.message

            /* otherwise, diplay the generic error message */
            : err.message});

    }
}
