import React from 'react';

export default function MultiDropDown() {
    state = {
      devices: [],
      selectedDevice: "",
      validationError: ""
    };

     {

        fetch(
            '/api/deviceType'
          )
            .then(response => {
              return response.json();
            })
            .then(data => {
              let deviceFromApi = data.map(device => {
                return { value: device, display: device };
              });
              this.setState({
                devices: [
                  {
                    value: "",
                    display:
                      "(Select your device)"
                  }
                ].concat(devicesFromApi)
              });
            })
            .catch(error => {
              console.log(error);
            });
    };

   return (
            <div>
            <select
                value={this.state.selectedDevice}
                onChange={e =>
                this.setState({
                    selectedTeam: e.target.value,
                    validationError:
                    e.target.value === ""
                        ? "You must select your favourite device"
                        : ""
                })
                }
            >
                {this.state.devices.map(device => (
                <option
                    key={device._id}
                    value={device.name}
                >
                    {device.display}
                </option>
                ))}
            </select>
            <div
                style={{
                color: "red",
                marginTop: "5px"
                }}
            >
                {this.state.validationError}
            </div>
            </div>
        );
  }
