import React, { useEffect, useState} from "react";



export default function Homescreen2(){
    {
    const state = {
      devices: [],
      selectedDevices: "",
      validationError: ""
    };

    function componentDidMount() {
      fetch(
        "/products/deciveType"
      )
        .then(response => {
          return response.json();
        })
        .then(data => {
          let devicesFromApi = data.map(device => {
            return { value: device, display: device };
          });
          this.setState({
            devices: [
              {
                value: "",
                display:
                  "(Select your device preference"
              }
            ].concat(devicesFromApi)
          });
        })
        .catch(error => {
          console.log(error);
        });
    }

    componentDidMount();

    function render() {

        <div>
          <select
            value={this.state.selectedDevices}
            onChange={e =>
              this.setState({
                selectedDevices: e.target.value,
                validationError:
                  e.target.value === ""
                    ? "You must select your preferred device"
                    : ""
              })
            }
          >
            {this.state.devices.map(device => (
              <option
                key={device._id}
                value={device.name}
              >
                {device.display}
              </option>
            ))}
          </select>
          <div
            style={{
              color: "red",
              marginTop: "5px"
            }}
          >
            {this.state.validationError}
          </div>
        </div>

    }
      return (

        render()

      );
    }
}
