// import { Axios } from "axios";
import axios from "axios";
import { PRODUCT_DETAILS_REQUEST, PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS } from "../constants/ProductConstants"

export const listProducts = () => async (dispatch) => {

    dispatch({type: PRODUCT_LIST_REQUEST});

    try{

        const {data} = await axios.get('/api/products');
        dispatch({type: PRODUCT_LIST_SUCCESS, payload:data});

    }

    catch(err){

        dispatch({type:PRODUCT_LIST_FAIL, payload:err.message});
    }
};

export const detailsProduct = (productId) => async (dispatch) => {

    dispatch({type: PRODUCT_DETAILS_REQUEST, payload: productId});

    try{

        const {data} = axios.get(`/api/products/${productId}`);
        dispatch({type: PRODUCT_DETAILS_SUCCESS, payload:data});
    }

    catch(err){

        dispatch({type: PRODUCT_DETAILS_FAIL, payload: err.response && err.response.data.message ? err.resonse.data.message: err.message});

    }
}
