class FavouriteTeam extends Component {
    state = {
      devices: [],
      selectedDevice: "",
      validationError: ""
    };

    componentDidMount() {

        fetch(
            '/api/deviceType'
          )
            .then(response => {
              return response.json();
            })
            .then(data => {
              let deviceFromApi = data.map(device => {
                return { value: device, display: device };
              });
              this.setState({
                devices: [
                  {
                    value: "",
                    display:
                      "(Select your device)"
                  }
                ].concat(devicesFromApi)
              });
            })
            .catch(error => {
              console.log(error);
            });
    }

    render() {
      return (
        <div>
        <select
          value={this.state.selectedDevice}
          onChange={e =>
            this.setState({
              selectedTeam: e.target.value,
              validationError:
                e.target.value === ""
                  ? "You must select your favourite device"
                  : ""
            })
          }
        >
          {this.state.devices.map(team => (
            <option
              key={device.value}
              value={device.value}
            >
              {team.display}
            </option>
          ))}
        </select>
        <div
          style={{
            color: "red",
            marginTop: "5px"
          }}
        >
          {this.state.validationError}
        </div>
      </div>
    );




    }
  }
