            );

            setLoading(false);

          }
        };
        fetchData();
        return () => {

            unmounted = true;
        };
      }, []);


    return(
    <div className="row top">
        <select
            disabled={loading}
            value={value}
            onChange={(e) => setValue(e.currentTarget.value)}
        >

            {data.map(({ label, value }) => (
                <option key={value} value={value}>
                {label}
                </option>
            ))}
        </select>
    </div>
    );
}
