class FavouriteTeam extends Component {
    state = {
      devices: [],
      selectedDevice: "",
      validationError: ""
    };

    componentDidMount() {

        fetch(
            '"http://localhost:26854/api/premiershipteams"'
          )
            .then(response => {
              return response.json();
            })
            .then(data => {
              let teamsFromApi = data.map(team => {
                return { value: team, display: team };
              });
              this.setState({
                teams: [
                  {
                    value: "",
                    display:
                      "(Select your favourite team)"
                  }
                ].concat(teamsFromApi)
              });
            })
            .catch(error => {
              console.log(error);
            });
    }

    render() {
      return (
        <div>
          <select></select>
        </div>
      );




