import { ADD_DROPDOWN_VALUE, SET_DROPDOWN_VALUES } from "../constants/ProductConstants"



//manages the options array
export const optionsReducer = (state= options, action)=>{
    switch(action.type){
        case ADD_DROPDOWN_VALUE:
            return [...state, action.payload]
        default:
        return state
    } 
}
